static bool isPdu1a(const char *str)
{
	if (str == NULL || *str == 0) {
		return false;
	}

	while (*str) {
		if (!isxdigit(*str)) {

			if (*str == 0x1a)
			{
				return true;
			}

			return false;
		}
		str++;
	}

	return false;
}

